package com.app.mou;

public class ToLowerCase {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String s="ss";
	
		if(s.charAt(1)>=97 && s.charAt(1)<=122){
				System.out.println("it is in lower case");
		}else{
			System.out.println("not in lower case");
		}
		
	}

}
